import Modal from "../UI/Modal";
import reactDom from "react-dom";

const Backdrop = () => {
  return <div style={{ backgroundColor: "rgba(0, 0, 0, 0.75)", height: "100vh" }}></div>;
};

const Cart = () => {
  return (
    <>
      {reactDom.createPortal(
        <Backdrop />,
        document.getElementById("backdrop-root")
      )}
      <Modal />
    </>
  );
};

export default Cart;
